// CSS Variable generator helper
export const generateCSSVariables = () => {
  const cssVars: Record<string, string> = {};

  // Generate color variables
  const flattenObject = (obj: any, prefix = ''): Record<string, string> => {
    return Object.entries(obj).reduce((acc, [key, value]) => {
      const varName = prefix ? `${prefix}-${key}` : key;

      if (typeof value === 'object' && value !== null && !Array.isArray(value)) {
        return { ...acc, ...flattenObject(value, varName) };
      }

      return { ...acc, [`--${varName}`]: String(value) };
    }, {});
  };

  return flattenObject(tokens);
};

// Utility to get CSS variable reference
export const cssVar = (path: string) => `var(--${path})`;

// Export ready-to-use CSS variables string
export const cssVariablesString = () => {
  const vars = generateCSSVariables();
  return Object.entries(vars)
    .map(([key, value]) => `  ${key}: ${value};`)
    .join('\n');
};
